package mypack;
import java.util.*;

public class ASCII {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        Scanner sc = new Scanner(System.in);
        char ch = sc.next().charAt(0);
        int ASCII = (int)ch;
        System.out.println(ch);
        System.out.println(ASCII);
        sc.close();
	}

}
